#!/usr/bin/env node

var stream = require('stream');
var util = require('util');
var _ = require('lodash');

function Test() {}


Test.prototype.room = function() {
  var s = new stream.Writable();
  s._write = function (chunk, encoding, done) {
    console.log(chunk.toString());
    done();
  };
  return s;
}

var test = new Test();
process.stdin.pipe(test.room());
